import React, { memo, useEffect, useRef, useState } from 'react';
import { useFormikContext } from 'formik';

import './style.scss';

const EditableInput = ({ name, label }) => {
  const [edit, setEdit] = useState(false);
  const { values, handleChange } = useFormikContext();
  const ref = useRef(null);
  const stopEditMode = e => (e.keyCode === 27 || 13 ? setEdit(false) : '');

  const handleClick = e => {
    if (ref.current?.contains(e.target)) return console.log('dentro');
    setEdit(false);
  };

  useEffect(() => {
    document.addEventListener('mousedown', handleClick);
    return () => {
      document.removeEventListener('mousedown', handleClick);
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);
  return (
    <div className='editable-input'>
      <h6>{label || name}</h6>
      {!edit ? (
        <h5 onClick={() => setEdit(true)}>{values[name] || `Your ${label}`}</h5>
      ) : (
        <input
          name={name}
          value={values[name]}
          onChange={handleChange}
          onKeyDown={stopEditMode}
          autoFocus
        />
      )}
    </div>
  );
};

export default memo(EditableInput);
