import React, { useEffect, useState } from 'react';
import Modal from 'react-modal';

const customStyles = {
  content: {
    border: 'none',
    width: '95%',
    display: 'flex',
    justifyContent: 'center',
    opacity: 0.1,
    background: 'transparent',
  },
};

const CustomModal = ({ openModal = false, closeModal }) => {
  const [mode, setMode] = useState(options[0]);
  useEffect(() => {
    Modal.setAppElement('body');
  }, []);
  return (
    <Modal
      onRequestClose={closeModal}
      isOpen={openModal}
      style={customStyles}
      ariaHideApp={false}
    >
      <div className='user-detail-page__modal'>
        <h4>My Profile Settings</h4>
        <ul className='list'>
          {options.map(o => (
            <li
              key={o}
              onClick={() => setMode(o)}
              className={mode === o ? 'selected-mode' : ''}
            >
              {o}
            </li>
          ))}
        </ul>
      </div>
    </Modal>
  );
};

const options = ['profile', 'security', 'account preferences'];

export default CustomModal;

