import React, { useEffect, useState } from 'react';
import { AiOutlineClose } from 'react-icons/ai';
import Modal from 'react-modal';

import './style.scss';
import Profile from './Profile';
import Security from './Security';
import useUserDetail from '../../../store/userDetails/actions';

const customStyles = {
  content: {
    border: 'none',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'flex-start',
    background: 'transparent',
  },
};

const CustomModal = ({ openModal = false, closeModal }) => {
  useEffect(() => {
    Modal.setAppElement('body');
  }, []);
  return (
    <Modal
      onRequestClose={closeModal}
      isOpen={openModal}
      style={customStyles}
      ariaHideApp={false}
    >
      <div className='user-detail-page-modal'>
        <div className='top pdd-base'>
          <h4>Security</h4>
          <AiOutlineClose className='pointer' onClick={closeModal} />
        </div>

        <div className='user-detail-page-modal__content pdd-base'>
          {mode === 'profile' && <Profile />}
          {mode === 'security' && <Security />}
        </div>
      </div>
    </Modal>
  );
};

const options = ['profile', 'security'];

export default CustomModal;
