import actions from './constants';
import axios from '../../api/axios';
import { UserDetailsStore } from './store';

export default function useActions() {
  const [state, dispatch] = UserDetailsStore.useUserDetails();

  const baseUrl = (action, id) => `user/${action}${id && '/' + id}`;

  const getData = async id => {
    try {
      const { data } = await axios.get(baseUrl('read', id));
      dispatch({
        type: actions.SET_USER,
        payload: data.user,
      });
    } catch (error) {
      console.log(error);
    }
  };

  const updateUser = async values => {
    try {
      await axios.patch(baseUrl('update', values.id), values);
    } catch (error) {
      console.log(error);
    }
  };

  const changePasswords = async values => {};

  return {
    changePasswords,
    getData,
    state,
    updateUser,
  };
}
