import React, { memo, useRef, useState } from 'react';

const Detail = ({ name, label, value }) => {
  const [edit, setEdit] = useState(false);
  const ref = useRef(null);
  const stopEditMode = e => e === 27 && setEdit(false);

  return (
    <div className='user-detail-page__detail'>
      <h6>{label || name}</h6>
      {!edit ? (
        <h5 onClick={() => setEdit(true)}>{value || `Your ${label}`}</h5>
      ) : (
        <input value={value} ref={ref} onKeyDown={stopEditMode} />
      )}
    </div>
  );
};

export default memo(Detail);
