import React from 'react';
import Avatar from './Avatar';
import Detail from './Detail';

import './style.scss';

const UserDetail = () => {
  return (
    <div className='user-detail-page monkeys-p-5'>
      <div className='user-detail-page__header'>
        <h3>Profile and visibility</h3>
        <p>
          Manage your personal information, and control which information other
          people see and apps may access.
        </p>
        <a className='pointer'>Learn more about your profile</a>
        <div className='user-detail-page__avatar-container'>
          <h4>Profile photo and header image</h4>
          <div className='user-detail-page__avatar-container__img-container'>
            <Avatar />
          </div>
        </div>
      </div>
      <div className='user-detail-page__content'>
        <h4>About you</h4>
        {details.map((item, i) => (
          <Detail {...item} key={i} />
        ))}
        <button>Edit</button>
      </div>
    </div>
  );
};

const details = [
  { name: 'fullName', label: 'Full Name' },
  { name: 'publicName', label: 'Public Name' },
  { name: 'jobTitle', label: 'Job Title' },
  { name: 'departament' },
  { name: 'organization' },
  { name: 'location' },
  { name: 'company' },
];

export default UserDetail;
