import React, { useEffect, useState } from 'react';
import { AiOutlineClose } from 'react-icons/ai';
import Modal from 'react-modal';

import './style.scss';
import Profile from './Profile';
import Security from './Security';
import useUserDetail from '../../../store/userDetails/actions';

const customStyles = {
  content: {
    border: 'none',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'flex-start',
    background: 'transparent',
  },
};

const CustomModal = ({ openModal = false, closeModal }) => {
  const [mode, setMode] = useState(options[0]);
  const {
    state: { user },
  } = useUserDetail();

  useEffect(() => {
    Modal.setAppElement('body');
  }, []);
  return (
    <Modal
      onRequestClose={closeModal}
      isOpen={openModal}
      style={customStyles}
      ariaHideApp={false}
    >
      <div className='user-detail-page-modal'>
        <div className='top pdd-base'>
          <h4>{mode === 'profile' ? 'My Profile Settings' : mode}</h4>
          <AiOutlineClose className='pointer' onClick={closeModal} />
        </div>

        <ul className='list'>
          {options.map(o => (
            <li
              key={o}
              onClick={() => setMode(o)}
              className={mode === o ? 'selected-mode' : ''}
            >
              {o}
            </li>
          ))}
        </ul>
        <div className='user-detail-page-modal__content pdd-base'>
          {mode === 'profile' && <Profile user={user} />}
          {mode === 'security' && <Security />}
        </div>
      </div>
    </Modal>
  );
};

const options = ['profile', 'security', 'account preferences'];

export default CustomModal;
